import React, {useState, useRef} from 'react';
import { Card, CardContent, makeStyles, Grid, TextField, Button} from '@material-ui/core';
import QRCode from 'qrcode';
import QrReader from 'react-qr-reader';

import logo from './bf.png';


function App() { 
  const [text, setText] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [scanResultFile, setScanResultFile] = useState('');
  const [scanResultWebCam, setScanResultWebCam] =  useState('');
  const classes = useStyles();
  const qrRef = useRef(null);





  const generateQrCode = async () => {
    try {
          const response = await QRCode.toDataURL(text);
          setImageUrl(response);
    }catch (error) {
      console.log(error);
    }
  }
  const handleErrorFile = (error) => {
    console.log(error);
  }
  const handleScanFile = (result) => {
      if (result) {
          setScanResultFile(result);
      }
  }
  const onScanFile = () => {
    qrRef.current.openImageDialog();
  }
  const handleErrorWebCam = (error) => {
    console.log(error);
  }
  const handleScanWebCam = (result) => {
    if (result){
        setScanResultWebCam(result);
    }
   }
function genr(){
  document.getElementById('ff').setAttribute('style','display:none')



  document.getElementById('dd').removeAttribute('style')
  document.getElementById('d').removeAttribute('style')

}

function scn(){
  document.getElementById('dd').setAttribute('style','display:none')
  document.getElementById('d').setAttribute('style','display:none')

  document.getElementById('ff').removeAttribute('style')


}

  return (
    <div style={{width:'100%',display:'flex',alignItems: 'center',flexDirection:'column'}}>
          <div className={classes.navbar} >
      <img  className={classes.img} src={logo} alt=""></img>
      <p onClick={genr} className={classes.el} >QR code generator</p>
      <p onClick={scn} className={classes.el} >QR code scanner</p>
    </div>
   <div  className={classes.w}  id='dd'>
   <QrReader 
                         delay={300}
                         onError={handleErrorWebCam}
                         onScan={handleScanWebCam}
                         />
   </div>

   <h3 id='d'  className={classes.b}  >Scanned result: {scanResultWebCam}</h3>





<div id='ff' style={{display:'none'}}>
<TextField  label="Enter Text Here" onChange={(e) => setText(e.target.value)}/>
                          <Button className={classes.btn} variant="contained" 
                            color="primary" onClick={() => generateQrCode()}>Generate</Button>
                            <br/>
                            <br/>
                            <br/>
                            {imageUrl ? (
                              <a href={imageUrl} download>
                                  <img style={{width:'69%',marginLeft: '35px'}} src={imageUrl} alt="img"/>
                              </a>) : null}

</div>





    </div>
  );
}

const useStyles = makeStyles((theme) => ({
    conatiner: {
      marginTop: 10
    },
    title: {
      display: 'flex',
      justifyContent: 'center',
      alignItems:  'center',
      background: '#3f51b5',
      color: '#fff',
      padding: 20
    },
    btn : {
      marginTop: 10,
      marginBottom: 20
    },
    r:{
      backgroundColor: 'red',
      width: '500px',
      height:'100px'
    },
    img:{
      cursor:'pointer',
      width: '7%',
  },
  navbar:{
    display: 'flex',
    width: '90%',
    alignItems: 'center',
    marginTop: '10px'
},
el:{
  margin: "0 8%",color: "#1e7f9d", fontSize: "20px", fontWeight: "500",cursor:"pointer"
},
w:{
  width: '30%',
},
b:{
  color:'rgb(19, 80, 131)'
}


}));
export default App;
